class Api {
  constructor({ baseUrl, authorizationCode }) {
    this._baseUrl = baseUrl;
    this._authorization = authorizationCode;
  }

  _checkResponse(res) {
    if (!res.ok) {
      return Promise.reject(res.status);
    }
    return res.json();
  }

  getUserInfoApi(path) {
    return fetch(`${this._baseUrl}${path}`, {
      headers: { authorization: this._authorization },
    }).then(this._checkResponse);
  }

  updateUserData(path, name, about) {
    return fetch(`${this._baseUrl}${path}`, {
      method: 'PATCH',
      headers: {
        authorization: this._authorization,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        name: name,
        about: about,
      }),
    }).then(this._checkResponse);
  }

  getInitialCard(path) {
    return fetch(`${this._baseUrl}${path}`, {
      headers: { authorization: this._authorization },
    }).then(this._checkResponse);
  }

  postCard(path, name, link) {
    return fetch(`${this._baseUrl}${path}`, {
      method: 'POST',
      headers: {
        authorization: this._authorization,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        name: name,
        link: link,
      }),
    }).then(this._checkResponse);
  }

  deleteCard(cardId) {
    return fetch(`${this._baseUrl}${cardId}`, {
      method: 'DELETE',
      headers: {
        authorization: this._authorization,
      },
    });
  }

  addLike(path) {
    return fetch(`${this._baseUrl}${path}`, {
      method: 'PUT',
      headers: { authorization: this._authorization },
    }).then(this._checkResponse);
  }
  removeLike(path) {
    return fetch(`${this._baseUrl}${path}`, {
      method: 'DELETE',
      headers: { authorization: this._authorization },
    }).then(this._checkResponse);
  }

  editProfileImage(path, avatar) {
    return fetch(`${this._baseUrl}${path}`, {
      method: 'PATCH',
      headers: {
        authorization: this._authorization,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        avatar: avatar,
      }),
    }).then(this._checkResponse);
  }
}

const api = new Api({
  baseUrl: 'https://around.nomoreparties.co/v1/group-12',
  authorizationCode: '37c0271e-6c35-4cdb-bfdd-3d6b737f9411',
});

export default api;
