import PopupWithForm from "./PopupWithForm";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import React, { useState } from "react";

function EditProfilePopup(props) {
  const currentUser = React.useContext(CurrentUserContext);

  const [name, setName] = useState(currentUser.name);
  const [description, setDescription] = useState(currentUser.about);

  function handleNameChange(evt) {
    setName(`${evt.target.value}`);
    console.log(name);
  }

  function handleDescriptionChange(evt) {
    setDescription(`${evt.target.value}`);
  }

  return (
    <PopupWithForm
      name="editProfilePopup"
      title="Edit profile"
      popupName="profile-popup"
      onClose={props.onClose}
      isOpen={props.isOpen}
      buttonText="Save"
    >
      <input
        value={name}
        onChange={handleNameChange}
        id="name-input"
        className="popup__input profile-form__item profile-form__name"
        name="Name"
        type="text"
        placeholder="Name"
        minLength="2"
        maxLength="40"
        required
      />
      <span className="name-input-error input-error"></span>
      <input
        value={description}
        onChange={handleDescriptionChange}
        id="about-me-input"
        className="popup__input profile-form__item profile-form__about-me"
        name="AboutMe"
        type="text"
        placeholder="About me"
        minLength="2"
        maxLength="200"
        required
      />
      <span className="about-me-input-error input-error"></span>
    </PopupWithForm>
  );
}

export default EditProfilePopup;
