class EditAvatarPopup extends React.Component {
  constructor(props) {
    super(props);
    this.avatarInput = React.createRef(
      "https://media-cldnry.s-nbcnews.com/image/upload/t_fit-1500w,f_auto,q_auto:best/newscms/2015_18/1005556/150501-manny-pacquiao-351.jpg"
    );
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleSubmit(e) {
    e.preventDefault();
    const x = this.avatarInput.current.value;
    this.props.onUpdateAvatar(x);
    console.log(this.avatarInput.current);
  }

  render() {
    return (
      <PopupWithForm
        name="EditAvatarPopup"
        title="Change profile picture"
        popupName="profile-image-popup"
        ref={this.avatarInput}
        onSubmit={this.handleSubmit}
        onClose={this.props.onClose}
        isOpen={this.props.isOpen}
        buttonText="Save"
      >
        <input
          id="name-input"
          className="popup__input profile-form__item profile-form__name"
          name="Name"
          type="text"
          placeholder="Profile Image URL"
          minLength="2"
          maxLength="40"
          required
        />
        <span className="name-input-error input-error"></span>
      </PopupWithForm>
    );
  }
}

export default EditAvatarPopup;
