class Auth {
  constructor({ baseUrl }) {
    this._baseUrl = baseUrl;
  }

  _checkResponse(res) {
    if (!res.ok) {
      return Promise.reject(res.status);
    }
    return res.json();
  }

  _postMethod(path, email, password) {
    return fetch(`${this._baseUrl}/${path}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, password }),
    }).then(this._getResponseData);
  }

  register = (email, password) => {
    return this._postMethod('signup', email, password);
  };

  authorize = (email, password) => {
    return this._postMethod('signin', email, password);
  };

  getAuthorizedContent = (token) => {
    return fetch(`${this._baseUrl}/users/me`, {
      method: 'GET',

      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
    }).then(this._getResponseData);
  };
}

const auth = new Auth({ baseUrl: 'https://register.nomoreparties.co' });

export default auth;
