import { Route, Switch, Redirect, withRouter } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import Header from './Header';
import Main from './Main';
import Login from './Login';
import Register from './Register';
import Footer from './Footer';
import ImagePopup from './ImagePopup';
import AddPlacePopup from './AddPlacePopup';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import SuccessMassege from './SuccessMassege';
import ErrorMassege from './ErrorMassege';
import api from '../utils.js/api';
import auth from '../utils.js/auth';

import '../index.css';

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isSelectedCardPopupOpen, setIsSelectedCardPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({ name: '', link: '' });
  const [currentUser, setCurrentUser] = useState({});
  const [cards, setCards] = useState([]);
  const [loggedIn, setLoggedIn] = useState(false);
  const [isSuccessMassegeOpen, setIsSuccessMassegeOpen] = useState(false);
  const [isErrorMassegeOpen, setIsErrorMassegeOpen] = useState(false);
  const [userData, setUserData] = useState([]);

  useEffect(() => {
    api
      .getInitialCard('/cards')
      .then((cards) => {
        setCards(cards);
      })
      .catch((err) => {
        console.log('Oops: ', err);
      });
  }, []);

  useEffect(() => {
    tokenCheck();
  }, []);

  function tokenCheck() {
    const jwt = localStorage.getItem('jwt');
    if (jwt) {
      auth
        .getAuthorizaedContent(jwt)
        .then((data) => {
          setUserData(data.data);
          console.log(data, `ori : ${userData}`);
        })
        .catch((err) => console.log(err));
    }
  }

  useEffect(() => {
    api
      .getUserInfoApi('/users/me')
      .then((data) => {
        setCurrentUser(data);
      })
      .catch((err) => {
        console.log('Oops: ', err);
      });
  }, []);

  function handleCardLike(card) {
    const isLiked = card.likes.some((user) => user._id === currentUser._id);

    !isLiked
      ? api
          .addLike(`/cards/likes/${card._id}`)
          .then((newCard) => {
            setCards((state) =>
              state.map((currentCard) =>
                currentCard._id === card._id ? newCard : currentCard,
              ),
            );
          })
          .catch((err) => {
            console.log(`Oops, error: ${err} !`);
          })
      : api
          .removeLike(`/cards/likes/${card._id}`)
          .then((newCard) => {
            setCards((state) =>
              state.map((currentCard) =>
                currentCard._id === card._id ? newCard : currentCard,
              ),
            );
          })
          .catch((err) => {
            console.log('Oops: ', err);
          });
  }

  function handleCardDelete(card) {
    api
      .deleteCard(`/cards/${card._id}`)
      .then(() => {
        setCards(
          cards.filter((cardInList) => {
            return cardInList._id !== card._id;
          }),
        );
      })
      .catch((err) => {
        console.log(`Oops, error: ${err} !`);
      });
  }

  function handleUpdateUser(name, about) {
    api
      .updateUserData('/users/me', name, about)
      .then((data) => {
        setCurrentUser(data);
        closeEditProfile();
      })
      .catch((err) => {
        console.log(`Oops, error: ${err} !`);
      });
  }

  function handleUpdateAvatar(avatar) {
    api
      .editProfileImage('/users/me/avatar', avatar)
      .then((data) => {
        console.log(data);
        setCurrentUser(data);
        closeEditAvatar();
      })
      .catch((err) => {
        console.log(`Oops, error: ${err} !`);
      });
  }

  function handleAddPlaceSubmit(name, link) {
    api
      .postCard('/cards', name, link)
      .then((newCard) => {
        setCards([newCard, ...cards]);
        closeAddPlace();
      })
      .catch((err) => {
        console.log(`Oops, error: ${err} !`);
      });
  }

  function handleSubmit(evt) {
    evt.preventDefault();

    auth
      .register(email, password)
      .then((res) => {
        if (res.data.email === email) {
          setEmail('');
          setPassword('');
          props.openSuccessMassege();
          history.push('/login');
        } else {
          props.openErrorMassege();
        }
      })
      .catch((err) => {
        props.openErrorMassege();
        console.log(err);
      });
  }

  function onEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function onAddPlaceClick() {
    setIAddPlacePopupOpen(true);
  }

  function onEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleCardClick(card) {
    setIsSelectedCardPopupOpen(true);
    setSelectedCard(card);
  }

  function openSuccessMassege() {
    setIsSuccessMassegeOpen(true);
  }

  function openErrorMassege() {
    setIsErrorMassegeOpen(true);
  }

  function closeEditProfile() {
    setIsEditProfilePopupOpen(false);
  }

  function closeAddPlace() {
    setIAddPlacePopupOpen(false);
  }

  function closeEditAvatar() {
    setIsEditAvatarPopupOpen(false);
  }

  function closeSelectedCardPopup() {
    setIsSelectedCardPopupOpen(false);
  }

  function closeSuccessMassege() {
    setIsSuccessMassegeOpen(false);
  }

  function closeErrorMassege() {
    setIsErrorMassegeOpen(false);
  }

  function handleLogin() {
    setLoggedIn(true);
  }

  const main = (
    <Main
      onEditProfileClick={onEditProfileClick}
      onAddPlaceClick={onAddPlaceClick}
      onEditAvatarClick={onEditAvatarClick}
      handleCardClick={handleCardClick}
      cards={cards}
      onCardLike={handleCardLike}
      onCardDelete={handleCardDelete}
    />
  );

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className='body'>
        <Header
          userData={userData}
          loggedIn={loggedIn}
          setLoggedIn={setLoggedIn}
        />

        <Switch>
          <Route exact path='/'>
            {loggedIn ? main : <Redirect to='/login' />}
          </Route>
          <Route path='/login'>
            <Login
              handleLogin={handleLogin}
              openSuccessMassege={openSuccessMassege}
              openErrorMassege={openErrorMassege}
            />
          </Route>
          <Route path='/signup'>
            <Register
              openSuccessMassege={openSuccessMassege}
              openErrorMassege={openErrorMassege}
            />
          </Route>
        </Switch>

        <Footer />
        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeEditProfile}
          onUpdateUser={handleUpdateUser}
        />

        <AddPlacePopup
          isOpen={isAddPlacePopupOpen}
          onClose={closeAddPlace}
          onAddCard={handleAddPlaceSubmit}
        />

        <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={closeEditAvatar}
          onUpdateAvatar={handleUpdateAvatar}
        />

        <ImagePopup
          closeSelectedCardPopup={closeSelectedCardPopup}
          isOpen={isSelectedCardPopupOpen}
          onClose={closeEditAvatar}
          card={selectedCard}
        />
        <SuccessMassege
          isOpen={isSuccessMassegeOpen}
          onClose={closeSuccessMassege}
        />
        <ErrorMassege isOpen={isErrorMassegeOpen} onClose={closeErrorMassege} />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
