import SignForm from './SignForm';
import { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import * as auth from '../utils.js/auth';

export default function Login(props) {
  let history = useHistory();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  function handleEmailChange(evt) {
    setEmail(evt.target.value);
  }

  function handlePasswordChange(evt) {
    setPassword(evt.target.value);
  }

  function handleSubmit(evt) {
    evt.preventDefault();
    props.setIsMassegeOpen(true);
    auth
      .authorize(email, password)
      .then((res) => {
        if (res) {
          props.setIsSuccess(true);
          setEmail('');
          setPassword('');

          props.handleLogin();
          props.setIsMassegeOpen(true);
          history.push('/');
        } else {
          props.setIsSuccess(false);
        }
      })
      .catch((err) => {
        props.setIsSuccess(false);
        console.log(err);
      });
  }

  return (
    <SignForm title='Sign in' setEmail={setEmail} handleSubmit={handleSubmit}>
      <input
        className='sing-form__input'
        name='email'
        type='email'
        placeholder='Email'
        value={email}
        onChange={handleEmailChange}
      />
      <input
        className='sing-form__input'
        name='password'
        type='password'
        placeholder='Password'
        value={password}
        onChange={handlePasswordChange}
      />
      <button className='sign-form__button' type='submit'>
        Sign in
      </button>
      <Link className='sign-form__link' to='/signup'>
        Not a member yet? Sign up here!
      </Link>
    </SignForm>
  );
}
