function ImagePopup(props) {
  return (
    <div
      className={
        props.isOpen ? "popup image-popup" : "popup image-popup popup-hidden"
      }
    >
      <div className="image-popup__container">
        <button
          onClick={props.closeSelectedCardPopup}
          className="image-popup__close-button popup__close-button"
          type="button"
        />
        <img
          className="image-popup__image"
          src={props.card.link}
          alt="Selected Card"
        />
        <h4 className="image-popup__location">{props.card.name}</h4>
      </div>
    </div>
  );
}
export default ImagePopup;
