import React from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
function Card({ card, onClick, onCardLike, onCardDelete }) {
  const currentUser = React.useContext(CurrentUserContext);

  function isOwn(card) {
    return card.owner._id === currentUser._id;
  }

  function cardDeleteButtonClassName(card) {
    return isOwn(card) ? 'card__remove-button' : 'card__remove-button_hidden';
  }

  function isLiked(card) {
    console.log(card);
    console.log(currentUser);
    return card.likes.some((user) => user._id === currentUser._id);
  }

  function cardLikeButtonClassName(card) {
    return `card__like-button ${
      isLiked(card) ? 'card__like-button_active' : ''
    } `;
  }

  function handleLikeClick(card) {
    onCardLike(card);
  }

  function handleDeleteClick(card) {
    onCardDelete(card);
  }

  const cardContent = (
    <article className='card'>
      <img
        onClick={() => {
          onClick({ name: card.name, link: card.link });
        }}
        className='card__image'
        src={card.link}
        alt='Card Image'
      />
      <button
        onClick={() => {
          handleDeleteClick(card);
        }}
        className={cardDeleteButtonClassName(card)}
        type='button'
      />
      <div className='card__location-like-container'>
        <h2 className='card__location'>{card.name}</h2>
        <div>
          <button
            onClick={() => {
              handleLikeClick(card);
            }}
            className={cardLikeButtonClassName(card)}
            type='button'
          />
          <p className='card__likeCounte'>{card.likes.length}</p>
        </div>
      </div>
    </article>
  );
  return <li>{cardContent}</li>;
}

export default Card;
