import { useHistory } from 'react-router-dom';
import { Link, useLocation } from 'react-router-dom';

export default function InfoTooltip(props) {
  let history = useHistory(props);
  const location = useLocation();
  function signOut() {
    localStorage.removeItem('jwt');
    props.setLoggedIn(false);
    history.push('/login');
  }
  return (
    <>
      {location.pathname === '/login' ? (
        <Link className='infoTooltip__link' to='/signup'>
          Sign up
        </Link>
      ) : location.pathname === '/' ? (
        <div>
          {props.loggedIn && (
            <p className='header__user-email'>{props.userData.email}</p>
          )}
          {props.loggedIn && (
            <button className='header__log-out' type='button' onClick={signOut}>
              Log out
            </button>
          )}
        </div>
      ) : (
        <Link className='infoTooltip__link' to='/login'>
          Log in
        </Link>
      )}
    </>
  );
}
