import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import ImagePopup from "./ImagePopup";
import PopupWithForm from "./PopupWithForm";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import api from "../utils.js/api";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import { useState, useEffect } from "react";
import "../index.css";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isSelectedCardPopupOpen, setIsSelectedCardPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({ name: "", link: "" });
  const [currentUser, setCurrentUser] = useState(false);
  const [cards, setCards] = useState([]);

  useEffect(() => {
    api
      .getInitialCard("/cards")
      .then((cards) => {
        setCards(cards);
        console.log(cards);
      })
      .catch((err) => {
        console.log("Oops: ", err);
      });
  }, []);

  function handleCardLike(card) {
    const isLiked = card.likes.some((user) => user._id === currentUser._id);

    !isLiked
      ? api.addLike(`/cards/likes/${card._id}`).then((newCard) => {
          setCards((state) =>
            state.map((currentCard) =>
              currentCard._id === card._id ? newCard : currentCard
            )
          );
        })
      : api.removeLike(`/cards/likes/${card._id}`).then((newCard) => {
          setCards((state) =>
            state.map((currentCard) =>
              currentCard._id === card._id ? newCard : currentCard
            )
          );
        });
  }

  function handleCardDelete(card) {
    api
      .deleteCard(`/cards/${card._id}`)
      .then(() => {
        card.removeCardFromDOM();
      })
      .catch((err) => {
        console.log(`Oops, error: ${err} !`);
      });
  }

  useEffect(() => {
    Promise.all([api.getUserInfoApi("/users/me"), api.getInitialCard("/cards")])
      .then(([data, cards]) => {
        setCurrentUser(data);
        console.log(cards);
      })
      .catch((err) => {
        console.log("Oops: ", err);
      });
  }, []);

  function handleUpdateUser(name, about) {
    api
      .updateUserData("/users/me", name, about)
      .then((data) => {
        setCurrentUser(data);
        closeEditProfile();
      })
      .catch((err) => {
        console.log(`Oops, error: ${err} !`);
      });
  }

  function handleUpdateAvatar(avatar) {
    api
      .editProfileImage("/users/me/avatar", avatar)
      .then((data) => {
        setCurrentUser(data);
        closeEditAvatar();
      })
      .catch((err) => {
        console.log(`Oops, error: ${err} !`);
      });
  }

  function onEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function onAddPlaceClick() {
    setIAddPlacePopupOpen(true);
  }

  function onEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleCardClick(card) {
    setIsSelectedCardPopupOpen(true);
    setSelectedCard(card);
  }

  function closeEditProfile() {
    setIsEditProfilePopupOpen(false);
  }

  function closeAddPlace() {
    setIAddPlacePopupOpen(false);
  }

  function closeEditAvatar() {
    setIsEditAvatarPopupOpen(false);
  }

  function closeSelectedCardPopup() {
    setIsSelectedCardPopupOpen(false);
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="body">
        <Header />
        <Main
          onEditProfileClick={onEditProfileClick}
          onAddPlaceClick={onAddPlaceClick}
          onEditAvatarClick={onEditAvatarClick}
          handleCardClick={handleCardClick}
          cards={cards}
          onCardLike={handleCardLike}
          onCardDelete={handleCardDelete}
        />
        <Footer />
        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeEditProfile}
          onUpdateUser={handleUpdateUser}
        />
        <PopupWithForm
          name="addPlacePopup"
          title="New Place"
          popupName="card-popup"
          onClose={closeAddPlace}
          isOpen={isAddPlacePopupOpen}
          buttonText="Save"
        >
          <input
            id="name-input"
            className="popup__input profile-form__item profile-form__name"
            name="Name"
            type="text"
            placeholder="Title"
            minLength="2"
            maxLength="40"
            required
          />
          <span className="name-input-error input-error"></span>
          <input
            id="about-me-input"
            className="popup__input profile-form__item profile-form__about-me"
            name="AboutMe"
            type="text"
            placeholder="Image URL"
            minLength="2"
            maxLength="200"
            required
          />
          <span className="about-me-input-error input-error"></span>
        </PopupWithForm>

        <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={closeEditAvatar}
          onUpdateAvatar={handleUpdateAvatar}
        />

        <ImagePopup
          closeSelectedCardPopup={closeSelectedCardPopup}
          isOpen={isSelectedCardPopupOpen}
          card={selectedCard}
        />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
