import SignForm from './SignForm';
import { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import * as auth from '../utils.js/auth';
import PopupMassege from './PopupMassege';

export default function Register(props) {
  let history = useHistory();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  function handleEmailChange(evt) {
    setEmail(evt.target.value);
  }

  function handlePasswordChange(evt) {
    setPassword(evt.target.value);
  }

  function handleSubmit(evt) {
    evt.preventDefault();
    console.log('ori');
    auth
      .register(email, password)
      .then((res) => {
        if (res.status === 200 || 201 || 204) {
          setEmail('');
          setPassword('');
          props.openSuccessMassege();

          history.push('/login');
        } else {
          props.openErrorMassege();
        }
      })
      .catch((err) => {
        props.openErrorMassege();
        console.log(err);
      });
  }

  return (
    <SignForm title='Sign up' handleSubmit={handleSubmit}>
      <input
        className='sing-form__input'
        name='Email'
        type='email'
        placeholder='Email'
        value={email}
        onChange={handleEmailChange}
      />
      <input
        className='sing-form__input'
        name='Password'
        type='password'
        placeholder='Password'
        value={password}
        onChange={handlePasswordChange}
      />
      <button className='sign-form__button' type='submit'>
        Sign up
      </button>
      <Link className='sign-form__link' to='/login'>
        Already a member? Log in here!
      </Link>
    </SignForm>
  );
}
