const BASE_URL = 'https://register.nomoreparties.co';

export const register = (email, password) => {
  return fetch(`${BASE_URL}/signup`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ email, password }),
  }).then((response) => {
    return response.json();
  });
};

export const authorize = (email, password) => {
  return fetch(`${BASE_URL}/signin`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ email, password }),
  })
    .then((response) => response.json())
    .then((data) => {
      if (data.token) {
        localStorage.removeItem('jwt');
        localStorage.setItem('jwt', data.token);
        console.log('ori:', data);
        return data;
      } else {
        return data;
      }
    })
    .catch((err) => {
      console.log(err);
    });
};

export const getAuthorizaedContent = (token) => {
  return fetch(`${BASE_URL}/users/me`, {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
      Authorization: `Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MzMwNDY4YjYzOTBhNDAwMTQ2OTk2Y2QiLCJpYXQiOjE2NjQyODM3MDB9.JpM4uOOej98gtogHLsZYofzF3JUJ9zoro6dTGpOPrXU`,
    },
  })
    .then((response) => {
      return response.json();
    })
    .then((res) => {
      console.log(localStorage.getItem('jwt'));
      return res;
    })
    .catch((err) => console.log(err));
};
