import React from "react";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
function InitialCards(props) {
  const currentUser = React.useContext(CurrentUserContext);

  const isOwn = card.owner._id === currentUser._id;

  const cardDeleteButtonClassName = `${
    isOwn ? "card__remove-button" : "card__remove-button_hidden"
  }`;

  const initialCardsList = props.cards.map((card) => (
    <li key={card._id}>
      <article className="card">
        <img
          onClick={() => {
            props.onClick({ name: card.name, link: card.link });
          }}
          className="card__image"
          src={card.link}
          alt="Card Image"
        />
        <button className={cardDeleteButtonClassName} type="button"></button>
        <div className="card__location-like-container">
          <h2 className="card__location">${card.name}</h2>
          <div>
            <button className="card__like-button" type="button"></button>
            <p className="card__likeCounte">{card.likes.length}</p>
          </div>
        </div>
      </article>
    </li>
  ));

  return <ul className="cards">{initialCardsList}</ul>;
}

export default InitialCards;
