import PopupWithForm from "./PopupWithForm";

import React, { useRef } from "react";

function EditAvatarPopup(props) {
  const avatarInput = useRef(
    "https://www.ringtv.com/wp-content/uploads/2020/08/pacquaio-ledwabaGettyImages-589679-770x571.jpg"
  );

  function handleSubmit(e) {
    e.preventDefault();

    props.onUpdateAvatar(avatarInput.current.value);
    console.log(avatarInput.current);
  }

  return (
    <PopupWithForm
      name="EditAvatarPopup"
      title="Change profile picture"
      popupName="profile-image-popup"
      ref={avatarInput}
      onSubmit={handleSubmit}
      onClose={props.onClose}
      isOpen={props.isOpen}
      buttonText="Save"
    >
      <input
        id="name-input"
        className="popup__input profile-form__item profile-form__name"
        name="Name"
        type="text"
        placeholder="Profile Image URL"
        minLength="2"
        maxLength="40"
        required
      />
      <span className="name-input-error input-error"></span>
    </PopupWithForm>
  );
}

