import api from "../utils.js/api";
import InitialCards from "./InitialCards";
import editIcon from "../images/edit.svg";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import React, { useState, useEffect } from "react";

function Main(props) {
  const currentUser = React.useContext(CurrentUserContext);
  const [cards, setCards] = useState([]);

  function handleCardLike(card) {
    const isLiked = card.likes.some((user) => user._id === currentUser._id);

    !isLiked
      ? api.addLike(`/cards/likes/${card._id}`).then((newCard) => {
          setCards((state) =>
            state.map((currentCard) =>
              currentCard._id === card._id ? newCard : currentCard
            )
          );
        })
      : api.removeLike(`/cards/likes/${card._id}`).then((newCard) => {
          setCards((state) =>
            state.map((currentCard) =>
              currentCard._id === card._id ? newCard : currentCard
            )
          );
        });
  }

  function handleCardDelete(card) {
    api
      .deleteCard(`/cards/${card._id}`)
      .then(() => {
        card.removeCardFromDOM();
      })
      .catch((err) => {
        console.log(`Oops, error: ${err} !`);
      })
      .finally(() => {
        profilePopupClass.hideLoading();
      });
  }

  useEffect(() => {
    api
      .getInitialCard("/cards")
      .then((cards) => {
        setCards(cards);
        console.log(cards);
      })
      .catch((err) => {
        console.log("Oops: ", err);
      });
  }, []);

  return (
    <main className="content">
      <section className="profile">
        <div className="profile__image-container">
          <img
            className="profile__image"
            src={currentUser.avatar}
            alt="profile image"
          />
          <div
            onClick={props.onEditAvatarClick}
            className="profile__edit-icon-container"
          >
            <img
              id="edit-icon"
              className="profile__edit-icon"
              src={editIcon}
              alt="Edit icon"
            />
          </div>
        </div>
        <div className="profile__info">
          <div className="profile__info-container">
            <h1 className="profile__name">{currentUser.name}</h1>
            <button
              onClick={props.onEditProfileClick}
              className="profile__edit-button"
              type="button"
            ></button>
          </div>
          <p className="profile__about-me">{currentUser.about}</p>
        </div>
        <button
          onClick={props.onAddPlaceClick}
          className="profile__add-button"
          type="button"
        >
          <span className="profile__add-button-sign"></span>
        </button>
      </section>
      <section>
        <InitialCards
          onClick={props.handleCardClick}
          cards={cards}
          onCardLike={handleCardLike}
          onCardDelete={handleCardDelete}
        />
      </section>
    </main>
  );
}

export default Main;
