function PopupWithForm({
  popupName,
  onClose,
  onSubmit,
  title,
  children,
  buttonText,
  isOpen,
}) {
  return (
    <div
      className={
        isOpen ? `popup ${popupName}` : `popup ${popupName} popup-hidden`
      }
    >
      <div className='popup__container'>
        <button
          onClick={onClose}
          className={`${popupName}__close-button popup__close-button`}
          type='button'
        />
        <form
          onSubmit={onSubmit}
          className='popup__form profile-form form'
          name='profileForm'
        >
          <h2 className='form__heading'>{title}</h2>
          <fieldset className='profile-form__fieldset form__fieldset'>
            {children}
            <button
              className='popup__button profile-form__submit-button'
              type='submit'
              value='Submit'
            >
              {buttonText}
            </button>
          </fieldset>
        </form>
      </div>
    </div>
  );
}

export default PopupWithForm;
