import api from "../utils.js/api";
import React, { useState, useEffect } from "react";

function Main(props) {
  const [userName, setUserName] = useState();
  const [userDescription, setUserDescription] = useState();
  const [userAvatar, setUserAvatar] = useState();
  const [cards, setCards] = useState([]);

  function set(e) {
    props.onCardClick(e.target.src);
  }

  const initialCardsList = cards.map((card) => (
    <li key={card._id} onClick={props.onCardClick(e.target.src)}>
      <article className="card">
        <img className="card__image" src={card.link} />
        <div className="card__location-like-container">
          <h2 className="card__location">${card.name}</h2>
          <div>
            <button className="card__like-button" type="button"></button>
            <p className="card__likeCounte">{card.likes.length}</p>
          </div>
        </div>
      </article>
    </li>
  ));

  useEffect(() => {
    Promise.all([
      api.getUserInfoApi("/users/me"),
      api.getInitialCard("/cards"),
    ]).then(([data, cards]) => {
      setUserName(data.name);
      setUserDescription(data.about);
      setUserAvatar(data.avatar);
      setCards(cards);
      console.log(data);
      console.log(cards);
    });
  });

  return (
    <main className="content">
      <section className="profile">
        <div className="profile__image-container">
          <img
            className="profile__image"
            src={userAvatar}
            alt="profile image"
          />
          <div className="profile__edit-icon-container">
            <img id="edit-icon" className="profile__edit-icon" src=" " />
          </div>
          <p className="profile__image-error"></p>
        </div>
        <div className="profile__info">
          <div className="profile__info-container">
            <h1 className="profile__name">{userName}</h1>
            <button className="profile__edit-button" type="button"></button>
          </div>
          <p className="profile__about-me">{userDescription}</p>
        </div>
        <button className="profile__add-button" type="button">
          <span className="profile__add-button-sign"></span>
        </button>
      </section>
      <section>
        <ul className="cards">{initialCardsList}</ul>
      </section>
    </main>
  );
}

export default Main;
