import { useState, useEffect } from 'react';
import logo from '../images/logo.svg';
import * as auth from '../utils.js/auth';

function Header() {
  const [userData, setUserData] = useState([]);
  useEffect(() => {
    tokenCheck();
  }, []);

  function tokenCheck() {
    const jwt = localStorage.getItem('jwt');
    if (jwt) {
      auth.getAuthorizaedContent(jwt).then((data) => {
        setUserData(data);
        console.log(data, `ori : ${userData}`);
      });
    }
  }
  return (
    <header className='header'>
      <img id='logo' className='header__logo' src={logo} alt='Around the us' />
      <p className='header__user-email'>{userData.data.email}</p>
      <button className='header__log-out' type='button'>
        Log out
      </button>
    </header>
  );
}

export default Header;
