import SignForm from './SignForm';
import { useState } from 'react';
import { Link } from 'react-router-dom';

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  function handleEmailChange(evt) {
    setEmail(evt.target.value);
  }

  function handlePasswordChange(evt) {
    setPassword(evt.target.value);
  }

  function handleSubmit(evt) {
    evt.preventDefault();
    auth
      .authorize(email, password)
      .then((data) => {
        if (data.jwt) {
          this.setState(
            {
              username: '',
              password: '',
            },
            () => {
              this.props.handleLogin();
              this.props.history.push('/diary');
            },
          );
        }
        // reset the state then in the callback, set the
        // parent App's loggedIn state to true,
        // then redirect to '/diary'
      })
      .catch((err) => console.log(err));
  }

  return (
    <SignForm title='Sign in' setEmail={setEmail}>
      <input
        className='sing-form__input'
        name='email'
        type='email'
        placeholder='Email'
        value={email}
        onChange={handleEmailChange}
      />
      <input
        className='sing-form__input'
        name='password'
        type='password'
        placeholder='Password'
        value={password}
        onChange={handlePasswordChange}
      />
      <button className='sign-form__button' type='submit'>
        Sign in
      </button>
      <Link className='sign-form__link' to='/signup'>
        Not a member yet? Sign up here!
      </Link>
    </SignForm>
  );
}
