import { Route, Switch, Redirect, useHistory } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import Header from './Header';
import Main from './Main';
import Login from './Login';
import Register from './Register';
import Footer from './Footer';
import ImagePopup from './ImagePopup';
import AddPlacePopup from './AddPlacePopup';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import Successmessage from './SuccessMessage';
import Errormessage from './ErrorMessage';
import api from '../utils.js/api';
import auth from '../utils.js/auth';

import '../index.css';

function App() {
  const history = useHistory();

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isSelectedCardPopupOpen, setIsSelectedCardPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({ name: '', link: '' });
  const [currentUser, setCurrentUser] = useState({});
  const [cards, setCards] = useState([]);
  const [loggedIn, setLoggedIn] = useState(false);
  const [isSuccessmessageOpen, setIsSuccessmessageOpen] = useState(false);
  const [isErrormessageOpen, setIsErrormessageOpen] = useState(false);
  const [userData, setUserData] = useState([]);

  useEffect(() => {
    const close = (e) => {
      if (e.keyCode === 27) {
        closeAddPlace();
        closeEditProfile();
      }
    };
    window.addEventListener('keydown', close);
    return () => window.removeEventListener('keydown', close);
  }, [AddPlacePopup, EditProfilePopup]);

  useEffect(() => {
    api
      .getInitialCard('/cards')
      .then((cards) => {
        setCards(cards);
      })
      .catch((err) => {
        console.log('Oops: ', err);
      });
  }, []);

  useEffect(() => {
    tokenCheck();
  }, []);

  useEffect(() => {
    api
      .getUserInfoApi('/users/me')
      .then((data) => {
        setCurrentUser(data);
      })
      .catch((err) => {
        console.log('Oops: ', err);
      });
  }, []);

  function handleCardLike(card) {
    const isLiked = card.likes.some((user) => user._id === currentUser._id);

    const handleNewCard = (newCard) => {
      setCards((state) =>
        state.map((currentCard) =>
          currentCard._id === card._id ? newCard : currentCard,
        ),
      );
    };

    !isLiked
      ? api
          .addLike(`/cards/likes/${card._id}`)
          .then(handleNewCard)
          .catch((err) => {
            console.log(`Oops, error: ${err} !`);
          })
      : api
          .removeLike(`/cards/likes/${card._id}`)
          .then(handleNewCard)
          .catch((err) => {
            console.log('Oops: ', err);
          });
  }

  function handleCardDelete(card) {
    const setUpdatedCardList = () => {
      setCards((state) =>
        state.filter((cardInList) => {
          return cardInList._id !== card._id;
        }),
      );
    };

    api
      .deleteCard(`/cards/${card._id}`)
      .then(setUpdatedCardList)
      .catch((err) => {
        console.log(`Oops, error: ${err} !`);
      });
  }

  function handleUpdateUser(name, about) {
    api
      .updateUserData('/users/me', name, about)
      .then((data) => {
        setCurrentUser(data);
        closeEditProfile();
      })
      .catch((err) => {
        console.log(`Oops, error: ${err} !`);
      });
  }

  function handleUpdateAvatar(avatar) {
    api
      .editProfileImage('/users/me/avatar', avatar)
      .then((data) => {
        setCurrentUser(data);
        closeEditAvatar();
      })
      .catch((err) => {
        console.log(`Oops, error: ${err} !`);
      });
  }

  function handleAddPlaceSubmit(name, link) {
    api
      .postCard('/cards', name, link)
      .then((newCard) => {
        setCards([newCard, ...cards]);
        closeAddPlace();
      })
      .catch((err) => {
        console.log(`Oops, error: ${err} !`);
      });
  }

  function handlRegister(email, password) {
    return auth
      .register(email, password)
      .then((res) => {
        if (res.data.email === email) {
          openSuccessmessage();
          history.push('/login');
        } else {
          openErrormessage();
        }
      })
      .catch((err) => {
        openErrormessage();
        console.log(err);
      });
  }

  function handleLogin(email, password) {
    return auth
      .authorize(email, password)
      .then((res) => {
        if (res.token) {
          localStorage.removeItem('jwt');
          localStorage.setItem('jwt', res.token);
          setLoggedIn(true);
          history.push('/');
        } else {
          openErrormessage();
        }
      })
      .catch((err) => {
        openErrormessage();
        console.log(err);
      });
  }

  function tokenCheck() {
    const jwt = localStorage.getItem('jwt');
    if (jwt) {
      auth
        .getAuthorizedContent(jwt)
        .then((data) => {
          setUserData(data.data);
          setLoggedIn(true);
        })
        .catch((err) => console.log(err));
    }
  }

  function onEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function onAddPlaceClick() {
    setIAddPlacePopupOpen(true);
  }

  function onEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleCardClick(card) {
    setIsSelectedCardPopupOpen(true);
    setSelectedCard(card);
  }

  function openSuccessmessage() {
    setIsSuccessmessageOpen(true);
  }

  function openErrormessage() {
    setIsErrormessageOpen(true);
  }

  function closeEditProfile() {
    setIsEditProfilePopupOpen(false);
  }

  function closeAddPlace() {
    setIAddPlacePopupOpen(false);
  }

  function closeEditAvatar() {
    setIsEditAvatarPopupOpen(false);
  }

  function closeSelectedCardPopup() {
    setIsSelectedCardPopupOpen(false);
  }

  function closeSuccessmessage() {
    setIsSuccessmessageOpen(false);
  }

  function closeErrormessage() {
    setIsErrormessageOpen(false);
  }

  const main = (
    <Main
      onEditProfileClick={onEditProfileClick}
      onAddPlaceClick={onAddPlaceClick}
      onEditAvatarClick={onEditAvatarClick}
      handleCardClick={handleCardClick}
      cards={cards}
      onCardLike={handleCardLike}
      onCardDelete={handleCardDelete}
    />
  );

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className='body'>
        <Header
          userData={userData}
          loggedIn={loggedIn}
          setLoggedIn={setLoggedIn}
        />

        <Switch>
          <Route exact path='/'>
            {loggedIn ? main : <Redirect to='/login' />}
          </Route>
          <Route path='/login'>
            <Login handleLogin={handleLogin} />
          </Route>
          <Route path='/signup'>
            <Register handlRegister={handlRegister} />
          </Route>
        </Switch>

        <Footer />
        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeEditProfile}
          onUpdateUser={handleUpdateUser}
        />

        <AddPlacePopup
          isOpen={isAddPlacePopupOpen}
          onClose={closeAddPlace}
          onAddCard={handleAddPlaceSubmit}
        />

        <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={closeEditAvatar}
          onUpdateAvatar={handleUpdateAvatar}
        />

        <ImagePopup
          closeSelectedCardPopup={closeSelectedCardPopup}
          isOpen={isSelectedCardPopupOpen}
          onClose={closeEditAvatar}
          card={selectedCard}
        />
        <Successmessage
          isOpen={isSuccessmessageOpen}
          onClose={closeSuccessmessage}
        />
        <Errormessage isOpen={isErrormessageOpen} onClose={closeErrormessage} />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
