import api from "../utils.js/api";
import InitialCards from "./InitialCards";
import editIcon from "../images/edit.svg";
import React, { useState, useEffect } from "react";

const currentUser = React.useContext(CurrentUserContext);

function Main(props) {
  const [cards, setCards] = useState([]);

  useEffect(() => {
    Promise.all([api.getUserInfoApi("/users/me"), api.getInitialCard("/cards")])
      .then(([data, cards]) => {
        setUserName(data.name);
        setUserDescription(data.about);
        setUserAvatar(data.avatar);
        setCards(cards);
      })
      .catch((err) => {
        console.log("Oops: ", err);
      });
  }, []);

  return (
    <main className="content">
      <section className="profile">
        <div className="profile__image-container">
          <img
            className="profile__image"
            src={currentUser.avatar}
            alt="profile image"
          />
          <div
            onClick={props.onEditAvatarClick}
            className="profile__edit-icon-container"
          >
            <img
              id="edit-icon"
              className="profile__edit-icon"
              src={editIcon}
              alt="Edit icon"
            />
          </div>
        </div>
        <div className="profile__info">
          <div className="profile__info-container">
            <h1 className="profile__name">{currentUser.name}</h1>
            <button
              onClick={props.onEditProfileClick}
              className="profile__edit-button"
              type="button"
            ></button>
          </div>
          <p className="profile__about-me">{currentUser.about}</p>
        </div>
        <button
          onClick={props.onAddPlaceClick}
          className="profile__add-button"
          type="button"
        >
          <span className="profile__add-button-sign"></span>
        </button>
      </section>
      <section>
        <InitialCards onClick={props.handleCardClick} cards={cards} />
      </section>
    </main>
  );
}

export default Main;
