import PopupWithForm from "./PopupWithForm";
import React, { useState, useEffect } from "react";

function AddPlacePopup(props) {
  const [name, setName] = useState("");
  const [link, setLink] = useState("");

  function handleNameChange(evt) {
    setName(evt.target.value);
  }

  function handleLinkChange(evt) {
    setLink(evt.target.value);
  }

  function handleSubmit(evt) {
    evt.preventDefault();
    props.onAddCard(name, link);
  }

  useEffect(() => {
    if (props.isOpen) {
      setName("");
      setLink("");
    }
  }, [props.isOpen]);

  return (
    <PopupWithForm
      name="addPlacePopup"
      title="New Place"
      popupName="card-popup"
      onClose={props.onClose}
      isOpen={props.isOpen}
      onSubmit={handleSubmit}
      buttonText="Save"
    >
      <input
        value={name}
        onChange={handleNameChange}
        id="name-input"
        className="popup__input profile-form__item profile-form__name"
        name="Name"
        type="text"
        placeholder="Title"
        minLength="2"
        maxLength="40"
        required
      />
      <span className="name-input-error input-error" />
      <input
        value={link}
        onChange={handleLinkChange}
        id="about-me-input"
        className="popup__input profile-form__item profile-form__about-me"
        name="link"
        type="text"
        placeholder="Image URL"
        minLength="2"
        maxLength="200"
        required
      />
      <span className="about-me-input-error input-error" />
    </PopupWithForm>
  );
}

export default AddPlacePopup;
