import { useHistory } from 'react-router-dom';
import { Link, useLocation } from 'react-router-dom';

export default function InfoTooltip(props) {
  let history = useHistory(props);

  const location = useLocation();

  const signupLink = (
    <Link className='infoTooltip__link' to='/signup'>
      Sign up
    </Link>
  );

  const loginLink = (
    <Link className='infoTooltip__link' to='/login'>
      Log in
    </Link>
  );

  const logoutLinkAndUserEmail = (
    <div className='infoTooltip__email-log-out-container'>
      <p className='infoTooltip__user-email'>{props.userData.email}</p>
      <button className='infoTooltip__log-out' type='button' onClick={signOut}>
        Log out
      </button>
    </div>
  );

  function signOut() {
    localStorage.removeItem('jwt');
    props.setLoggedIn(false);
    history.push('/login');
  }

  return (
    <>
      {location.pathname === '/login'
        ? signupLink
        : location.pathname === '/signup'
        ? loginLink
        : logoutLinkAndUserEmail}
    </>
  );
}
